mapper.update:
update mall_activity set status = 2 where begin_time <= NOW() - INTERVAL 1 HOUR and status = 1;
update mall_activity set status = 3 where begin_time <= NOW() and status = 1 or status = 2;
update mall_activity set status = 4 where end_time <= NOW() and status = 3;
update mall_activity set status = 5 where end_time <= NOW() + INTERVAL 1 HOUR and status = 4;
update mall_activity_goods set status = 2 where activity_id in ( select activity_id from mall_activity where status = 4 ) and status = 1

update mall_activity
addtime = #{addtime,jdbcType=TIMESTAMP}, status = #{status,jdbcType=INTEGER}, grade = #{grade,jdbcType=INTEGER}, begin_time = #{beginTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, title = #{title,jdbcType=VARCHAR}, sequence_code = #{sequenceCode,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, modify_date = #{modifyDate,jdbcType=TIMESTAMP}, modify_user_Id = #{modifyUserId,jdbcType=VARCHAR}, modify_user_name = #{modifyUserName,jdbcType=VARCHAR}, content = #{content,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select count(0) from mall_activity where 1=1
and title like concat('%',#{title,jdbcType=VARCHAR},'%')
and status = #{status,jdbcType=INTEGER}

SELECT id, sequence_code as sequenceCode, title, status, grade, begin_time as beginTime, end_time as endTime, create_user_name as createUserNaem, create_date as createDate, description FROM mall_activity where 1 = 1
and title like concat('%',#{title,jdbcType=VARCHAR},'%')
and status = #{status,jdbcType=INTEGER}
order by create_date desc
limit #{startIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
