mapper.update:
update mall_advert
position_type = #{positionType,jdbcType=INTEGER}, ad_type = #{adType,jdbcType=INTEGER}, ad_name = #{adName,jdbcType=VARCHAR}, ad_link = #{adLink,jdbcType=VARCHAR}, begin_time = #{beginTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, click_count = #{clickCount,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER}, link_man = #{linkMan,jdbcType=VARCHAR}, link_phone = #{linkPhone,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, modify_date = #{modifyDate,jdbcType=TIMESTAMP}, modify_user_Id = #{modifyUserId,jdbcType=VARCHAR}, modify_user_name = #{modifyUserName,jdbcType=VARCHAR}, img = #{img,jdbcType=VARCHAR}, obj_id = #{objId,jdbcType=VARCHAR}, position_code = #{positionCode,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

update mall_advert
set status = 4 
where id = #{id,jdbcType=VARCHAR}

mapper.select:
  	select count(0)
  	from mall_advert
  	where 1=1

  		and ad_name like concat('%',#{adName,jdbcType=VARCHAR},'%')


		and status = #{status,jdbcType=INTEGER}


		and position_Type = #{positionType,jdbcType=INTEGER}

SELECT ma.id, ma.position_code AS positionCode, ma.ad_name AS adName, ma.click_count AS clickCount, map.id AS positionId, map.position_name AS positionName, ma.position_type AS positionType, ma.link_man AS linkMan, ma.link_phone AS linkPhone, ma.status, ma.begin_time AS beginTime, ma.end_time AS endTime, ma.create_user_name AS createUserName, ma.create_date AS createDate, ma.description FROM mall_advert ma LEFT JOIN mall_advert_position map ON ma.position_code = map.position_code where 1=1
and ma.ad_name like concat('%',#{adName,jdbcType=VARCHAR},'%')
and ma.status = #{status,jdbcType=INTEGER}
and ma.position_Type = #{positionType,jdbcType=INTEGER}
order by ma.create_date desc
limit #{startIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
