mapper.select:
SELECT pa.id AS applyId, ci.realname AS realName, ci.mobile AS mobile, pa.sub_time AS subTime, pa.sub_by AS subBy, ps.sku_id AS skuId, ps.sku_name AS skuName, od.quantity AS quantity, ps.attributes AS attributes, ol.pay_type AS payType, pa.order_detail_id AS orderDetailId, pa.sub_type AS subType, pa.status AS status, od.price AS price, pa.description AS description, pa.conduct_by AS conductBy, pa.conduct_result AS conductResult, pa.conduct_time AS conductTime, ol.order_id AS orderId FROM product_aftersale_apply pa LEFT JOIN customer_info ci ON ci.c_id = pa.sub_by LEFT JOIN order_detail od ON pa.order_detail_id = od.odi_id LEFT JOIN order_list ol ON ol.order_id = od.order_id LEFT JOIN product_sku ps ON ps.sku_id = od.sku_id
AND pa.status = #{status}
AND pa.sub_time > concat(#{startDate}, ' 0:0:0')
AND pa.sub_time < concat(#{endDate}, ' 23:59:59')
ORDER BY sub_time DESC
limit #{start,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
