mapper.select:
SELECT pi.product_id AS productId, pi.product_name AS productName, pi.product_explain AS productExplain, pi.product_details AS productDetails, pi.brand_id AS brandId, pb.brand_name AS brandName, pi.product_price AS productPrice, pi.product_up_time AS productUpTime, pi.product_down_time AS productDownTime, pi.category_Id AS categoryId, pc.cname AS cname, pi.product_type AS productType FROM product_info pi LEFT JOIN product_category pc ON pi.category_Id = pc.category_Id LEFT JOIN product_brand pb ON pi.brand_id = pb.brand_id WHERE pi.review_status = 2
AND pi.product_name like CONCAT('%', #{productName}, '%')
LIMIT #{start,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

mapper.update:
update product_info
product_name = #{productName,jdbcType=INTEGER}, product_explain = #{productExplain,jdbcType=VARCHAR}, review_status = #{reviewStatus,jdbcType=INTEGER}, brand_id = #{brandId,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, modify_date = #{modifyDate,jdbcType=TIMESTAMP}, modify_user_Id = #{modifyUserId,jdbcType=VARCHAR}, modify_user_name = #{modifyUserName,jdbcType=VARCHAR}, product_price = #{productPrice,jdbcType=INTEGER}, product_up_time = #{productUpTime,jdbcType=VARCHAR}, product_down_time = #{productDownTime,jdbcType=VARCHAR}, category_Id = #{categoryId,jdbcType=VARCHAR}, product_details = #{productDetails,jdbcType=VARCHAR}, product_type = #{productType,jdbcType=INTEGER},
where product_id = #{productId,jdbcType=VARCHAR}

mapper.select:
select count(0)
from product_info
where 1 = 1

	and product_name like CONCAT('%', #{productName,jdbcType=VARCHAR}, '%')


	and product_type = #{productType}


	and category_Id = #{categoryId}


	and review_status = #{reviewStatus}

SELECT pi.product_id AS productId, pi.product_name AS productName, pi.product_type as productType, pi.category_Id AS categoryId, pc.cname AS cname, pi.brand_id AS brandId, pb.brand_name AS brandName, pi.product_price AS productPrice, pi.review_status as reviewStatus, pi.description as description, pi.create_date as createDate, pi.create_user_Id as createUserId, pi.create_user_name as createUserName, pi.modify_date as modifyDate, pi.modify_user_Id as modifyUserId, pi.modify_user_name as modifyUserName, pi.product_up_time as productUpTime, pi.product_down_time as productDownTime, pi.product_type AS productType FROM product_info pi left join product_brand pb on pi.brand_id = pb.brand_id left join product_category pc on pi.category_Id = pc.category_Id WHERE 1 = 1
AND pi.product_name like CONCAT('%', #{productName,jdbcType=VARCHAR}, '%')
AND pi.product_type = #{productType}
AND pi.category_Id = #{categoryId}
AND pi.review_status = #{reviewStatus}
ORDER BY pi.create_date desc
LIMIT #{start,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT product_id, product_name, product_explain, review_status, brand_id, description, create_date, create_user_Id, create_user_name, modify_date, modify_user_Id, modify_user_name, product_price, product_up_time, product_down_time, category_Id, product_details, product_type FROM product_info info
WHERE product_type = #{recommendType,jdbcType=INTEGER}
ORDER BY modify_date DESC
LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT pi.product_id as productId, pi.category_Id as categoryId, pi.product_name as productName, pb.brand_name as brandName, pi.product_price as productPrice, pi.product_type AS productType FROM product_info pi LEFT JOIN product_brand pb ON pi.brand_id = pb.brand_id WHERE pi.review_status=2
AND pi.product_type = #{productType,jdbcType=INTEGER}
ORDER BY pi.modify_date DESC
LIMIT #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
