		$(function(){
			$('#test').datagrid({
				width:1000,
				height:350,
				nowrap: false,
				striped: true,
				collapsible:true,
				url:'datagrid_data.json',
				sortName: 'code',
				sortOrder: 'desc',
				remoteSort: false,
				idField:'code',
				frozenColumns:[[
	                {field:'ck',checkbox:true},
				]],
				columns:[[
						{field:'opt',title:'站点名称',width:196,align:'left', rowspan:2,
						formatter:function(value,rec){
							return '<span style="color:red">Edit Delete</span>';
						}
					}
				],[
					{field:'name',title:'站点名称',width:220},
					{field:'addr',title:'站点名称',width:220,rowspan:2,sortable:true,
						sorter:function(a,b){
							return (a>b?1:-1);
						}
					},
					{field:'col4',title:'Col41',width:270,rowspan:2}
				]],
				pagination:true,
				rownumbers:true,
				toolbar:[{
					id:'btnadd',
					text:'新 增',
					iconCls:'icon-add',
					handler:function(){
						$('#btnadd').linkbutton('enable');
						alert('add')
					}
				},{
					id:'btncancel',
					text:'删 除',
					iconCls:'icon-cancel',
					handler:function(){
						$('#btncancel').linkbutton('enable');
						alert('cancel')
					}
				},{
					id:'btnstatic',
					text:'目录静态化',
					iconCls:'icon-static',
					handler:function(){
						$('#btnstatic').linkbutton('enable');
						alert('static')
					}
				},{
					id:'btnmobile',
					text:'移 动',
					iconCls:'icon-mobile',
					handler:function(){
						$('#btnmobile').linkbutton('enable');
						alert('mobile')
					}
				},{
					id:'btnpreview',
					text:'预 览',
					iconCls:'icon-preview',
					handler:function(){
						$('#btnpreview').linkbutton('enable');
						alert('preview')
					}
				},{
					id:'btnmerge',
					text:'加入合并',
					iconCls:'icon-merge',
					handler:function(){
						$('#btnmerge').linkbutton('enable');
						alert('merge')
					}
				},{
					id:'btnshare',
					text:'共 享',
					iconCls:'icon-share',
					handler:function(){
						$('#btnshare').linkbutton('enable');
						alert('share')
					}
				},{
					id:'btncut',
					text:'剪 切',
					iconCls:'icon-cut',
					handler:function(){
						$('#btncut').linkbutton('enable');
						alert('cut')
					}
				},{
					id:'btnjournal',
					text:'生成日志列表',
					iconCls:'icon-journal',
					handler:function(){
						$('#btnjournal').linkbutton('enable');
						alert('journal')
					}
				}]
			});
			var p = $('#test').datagrid('getPager');
			$(p).pagination({
				onBeforeRefresh:function(){
					alert('before refresh');
				}
			});
		});
		function resize(){
			$('#test').datagrid('resize', {
				width:900,
				height:400
			});
		}
		function getSelected(){
			var selected = $('#test').datagrid('getSelected');
			if (selected){
				alert(selected.code+":"+selected.name+":"+selected.addr+":"+selected.col4);
			}
		}
		function getSelections(){
			var ids = [];
			var rows = $('#test').datagrid('getSelections');
			for(var i=0;i<rows.length;i++){
				ids.push(rows[i].code);
			}
			alert(ids.join(':'));
		}
		function clearSelections(){
			$('#test').datagrid('clearSelections');
		}
		function selectRow(){
			$('#test').datagrid('selectRow',2);
		}
		function selectRecord(){
			$('#test').datagrid('selectRecord','002');
		}
		function unselectRow(){
			$('#test').datagrid('unselectRow',2);
		}
		function mergeCells(){
			$('#test').datagrid('mergeCells',{
				index:2,
				field:'addr',
				rowspan:2,
				colspan:2
			});
		}

