		$(function(){
			$('#dd1').click(function(){
				$('#sb1,#sb2').splitbutton({plain:false});
				$('#mb3').menubutton({plain:false});
			});
			$('#dd2').click(function(){
				$('#sb1,#sb2').splitbutton({plain:true});
				$('#mb3').menubutton({plain:true});
			});
		});

