mapper.update:
update work_material_category
category_name = #{categoryName,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}
where wic_id = #{wicId,jdbcType=VARCHAR}

mapper.select:
	select wic_id, category_name, parent_id, code
  from work_material_category
  where wic_id = #{wicId,jdbcType=VARCHAR}

	select wic_id, category_name, parent_id, code
  from work_material_category
  where parent_id = #{parent_id,jdbcType=VARCHAR}
