mapper.update:
update coupon_inventory
set delete_mark = 1
where cr_id = #{crId,jdbcType=VARCHAR}

update coupon_inventory
coupon_name = #{couponName,jdbcType=VARCHAR}, coupon_type = #{couponType,jdbcType=INTEGER}, coupon_quantity = #{couponQuantity,jdbcType=INTEGER}, coupon_quantity_get = #{couponQuantityGet,jdbcType=INTEGER}, valid_time_start = #{validTimeStart,jdbcType=TIMESTAMP}, valid_time_end = #{validTimeEnd,jdbcType=TIMESTAMP}, face_value = #{faceValue,jdbcType=DECIMAL}, change_grade = #{changeGrade,jdbcType=INTEGER}, change_integral = #{changeIntegral,jdbcType=INTEGER}, pic_small = #{picSmall,jdbcType=VARCHAR}, pic_big = #{picBig,jdbcType=VARCHAR}, product_id = #{productId,jdbcType=VARCHAR}, delete_mark = #{deleteMark,jdbcType=INTEGER}, enabled_mark = #{enabledMark,jdbcType=INTEGER}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, modify_date = #{modifyDate,jdbcType=TIMESTAMP}, modify_user_Id = #{modifyUserId,jdbcType=VARCHAR}, modify_user_name = #{modifyUserName,jdbcType=VARCHAR}, coupon_explain = #{couponExplain,jdbcType=LONGVARCHAR}, service_coupon_type = #{serviceCouponType,jdbcType=INTEGER}, sku_id = #{skuId,jdbcType=VARCHAR}, discount = #{discount,jdbcType=DECIMAL}
where cr_id = #{crId,jdbcType=VARCHAR}

mapper.select:
SELECT cp.cr_id AS crId, cp.coupon_name AS couponName, cp.coupon_type AS couponType, cp.service_coupon_type AS serviceCouponType, cp.coupon_quantity AS couponQuantity, cp.coupon_quantity_get AS couponQuantityGet, cp.valid_time_start AS validTimeStart, cp.valid_time_end AS validTimeEnd, cp.face_value AS faceValue, cp.discount AS discount, cp.change_grade AS changeGrade, cp.change_integral AS changeIntegral, cp.pic_small AS picSmall, cp.pic_big AS picBig, cp.sku_id AS skuId, ps.sku_name AS skuName, cp.product_id AS productId, pi.product_name AS productName, cp.coupon_explain AS couponExplain, cp.enabled_mark AS enabledMark FROM coupon_inventory cp LEFT JOIN product_sku ps ON cp.sku_id = ps.sku_id LEFT JOIN product_info pi ON cp.product_id = pi.product_id WHERE cp.delete_mark = 0 AND cp.enabled_mark = 2
AND cp.coupon_type = #{couponType}
AND cp.valid_time_end >= NOW() order by cp.valid_time_end
limit 0, #{num}

SELECT cp.cr_id AS crId, cp.coupon_name AS couponName, CAST(cp.coupon_type AS CHAR) AS couponType, cp.service_coupon_type AS serviceCouponType, cp.coupon_quantity AS couponQuantity, cp.coupon_quantity_get AS couponQuantityGet, cp.valid_time_start AS validTimeStart, cp.valid_time_end AS validTimeEnd, cp.face_value AS faceValue, cp.discount AS discount, cp.change_grade AS changeGrade, CAST(cp.change_integral AS CHAR) AS changeIntegral, cp.pic_small AS picSmall, cp.pic_big AS picBig, cp.sku_id AS skuId, ps.sku_name AS skuName, cp.product_id AS productId, pi.product_name AS productName, cp.coupon_explain AS couponExplain FROM coupon_inventory cp LEFT JOIN product_sku ps ON cp.sku_id = ps.sku_id LEFT JOIN product_info pi ON cp.product_id = pi.product_id WHERE cp.delete_mark = 0 AND cp.enabled_mark = 2
AND cp.coupon_type = #{type,jdbcType=INTEGER}
AND cp.valid_time_end >= NOW() order by cp.valid_time_end
limit #{startIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
