mapper.select:
  	SELECT cate.*
    FROM product_category cate LEFT JOIN product_category pare on pare.category_Id = cate.parent_Id
	WHERE pare.category_key = #{parentKey,jdbcType=VARCHAR} 
	order by cate.sort desc

    	LIMIT 0, #{num,jdbcType=INTEGER}

mapper.update:
update product_category
parent_Id = #{parentId,jdbcType=VARCHAR}, category_key = #{categoryKey,jdbcType=VARCHAR}, cname = #{cname,jdbcType=VARCHAR}, sort = #{sort,jdbcType=INTEGER}, has_leaf = #{hasLeaf,jdbcType=INTEGER}, category_status = #{categoryStatus,jdbcType=INTEGER}, description = #{description,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, create_user_Id = #{createUserId,jdbcType=VARCHAR}, create_user_name = #{createUserName,jdbcType=VARCHAR}, modify_date = #{modifyDate,jdbcType=TIMESTAMP}, modify_user_Id = #{modifyUserId,jdbcType=VARCHAR}, modify_user_name = #{modifyUserName,jdbcType=VARCHAR}
where category_Id = #{categoryId,jdbcType=VARCHAR}
